import {Component, ElementRef, OnInit} from '@angular/core';
import LogicFlow from '@logicflow/core';
import {Control, Menu, DndPanel, SelectionSelect, BpmnElement, Snapshot, MiniMap} from '@logicflow/extension'
import {MenuItem} from "@logicflow/extension/src/components/menu";

@Component({
  selector: 'app-logic-flow',
  templateUrl: './logic-flow.component.html',
  styleUrls: ['./logic-flow.component.less']
})
export class LogicFlowComponent implements OnInit{

  constructor(private el: ElementRef,) {
  }
  public lf: any;

  public treeList = [
    {
      id: '3',
      name: '目录1',
      children: [
        {
          id: '31',
          type: 'assignment',
          name: '节点1-1',
          children: [],
        },
        {
          id: '34',
          type: 'decision',
          name: '节点1-2',
          children: [],
        },
        {
          id: '35',
          type: 'startParallel',
          name: '节点1-3',
          children: [],
        },
        {
          id: '36',
          type: 'endParallel',
          name: '节点1-4',
          children: [],
        },
      ],
    },
    {
      id: '4',
      name: '目录2',
      children: [
        {
          id: '41',
          type: 'machineLearning',
          name: '节点2-1',
          children: [],
        },
        {
          id: '42',
          type: 'deepLearning',
          name: '节点2-2',
          children: [],
        },
      ],
    },
  ];

  public ngOnInit() {
    const data: any = {
      // 节点
      nodes: [
        {
          id: 1,
          type: 'rect',
          x: 100,
          y: 200,
          text: {
            value: 'квадрат',
            x: 100,
            y: 200,
          },
        },
        {
          id: 2,
          type: 'circle',
          x: 300,
          y: 400,
          text: {
            value: 'круг',
            x: 300,
            y: 400,
          },
        },
      ],
      // 边
      edges: [
        {
          type: 'polyline',
          sourceNodeId: 1,
          targetNodeId: 2,
        },
      ],
    };

    this.lf = new LogicFlow({
      background: {
        backgroundColor: "#EEF5FF",
      },
      container: document.querySelector('#container')!,
      height: 800,
      // grid: true,
      plugins: [BpmnElement, Menu, Snapshot, MiniMap, DndPanel, SelectionSelect, Snapshot],
      pluginsOptions: {
        miniMap: {
        },
      },
    });



    this.lf.extension.menu.setMenuConfig({
      nodeMenu: [
        {
          className: "lf-menu-delete custom-menu",
          text: "Удалить блок",
          icon: true,
          callback: (node: any) => {
            this.lf.deleteNode(node.id);
          }
        },
      ],
      edgeMenu: [
        {
          text: "тест меню связи",
          callback(edge: any) {
            alert(`
          id：${edge.id}
          тип линии：${edge.type}
          координаты：(x: ${edge.x}, y: ${edge.y})
          sourceNodeId：${edge.sourceNodeId}
          targetNodeId：${edge.targetNodeId}`);
          }
        }
      ],
      graphMenu: [
        {
          text: 'руддщ',
          callback() {
            alert('руддщ！')
          },
        },
      ],
    });

    this.lf.extension.dndPanel.setPatternItems([
      {
        label: 'electoral district',
        icon: 'data:image/png;base64,',
        callback: () => {
          this.lf['openSelectionSelect']();
          this.lf.once('selection:selected', () => {
            this.lf['closeSelectionSelect']();
          });
        }
      },
      {
        type: 'circle',
        text: 'Кругляш',
        label: 'Кругляш',
        icon: 'data:image/png;base64,',
      },
      {
        type: 'rect',
        label: 'User',
        icon: 'data:image/png;base64,',
        className: 'important-node'
      },
      {
        type: 'polygon',
        label: 'Polygon',
        icon: 'data:image/png;base64,',
        className: 'important-node'
      },
      {
        type: 'diamond',
        label: 'diamond',
        icon: 'data:image/png;base64,',
      },
    ]);

    this.lf.render(data);
    let typeList = ['assignment', 'decision', 'startParallel', 'endParallel', 'machineLearning', 'deepLearning'];

    this.el.nativeElement.addEventListener('dragstart', (val: any) => {

      if (typeList.includes(val.type)) {
        // dragRow = val;
        // randomNum.value = randomNumber();
        this.lf.dnd.startDrag({
          type: val.type,
          text: val.name,
          id: Date.now(),
        });
      }
    });
  }

  public downloadScreen() {
    this.lf.extension.snapshot.customCssRules = `
    .lf-canvas-overlay {
      background: #EEF5FF;
    }`;

    this.lf.getSnapshot();
  }

  public resetGraph() {
    this.lf.resetZoom();
    this.lf.resetTranslate();
  }
}
